const Notification = (props) => {
  message, isError = [...props]
  console.log('isError', isError)
  console.log('message', message)
  if ( message === null) {
    return null
  }

  if (isError === true) {
    return (
      <div className='error'>
        {message}
      </div>
    )
  } else {
    return (
      <div className='message'>
        {message}
      </div>
    )
  }
}

export default Notification
