import { useState, useEffect } from 'react'
import personService from './services/persons'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import FilteredPersons from './components/FilteredPersons'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  useEffect(() => {
     personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    setFilter(event.target.value)
  }

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }

    // prevent duplicate names from being added
    if (nameIsNotAlreadyInPhonebook(newName)) {
      personService
        .create(personObject)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          setNewName('')
          setNewNumber('')
        })

    // update number if name is already in phonebook
    } else {
      const personId = persons.filter(person => person.name === newName)[0].id
      if (window.confirm(`${newName} is already added to phonebook, replace old number with new one?`)) {
        personService
          .update(personId, personObject)
          .then(returnedPerson => {
            console.log(`${newName}'s number has been updated to ${newNumber}`)
            console.log(returnedPerson)
            console.log('persons: ', persons)
            // do something here to reload page!
          })
        personService
          .getAll()
          .then(updatedPersons => {
            setPersons(updatedPersons)
          })
      }
    }
  }

  const nameIsNotAlreadyInPhonebook = (name) => {
    const names = persons.map(person => person.name)
    return !names.includes(name)
  }

  const removePerson = (id) => {
    const personToRemove = persons.filter(person => person.id === id)[0]
    console.log(personToRemove)
    if (window.confirm(`Are you sure you want to delete ${personToRemove.name}?`)) {
      personService
        .remove(id)
        console.log(`${personToRemove.name} was deleted`)
        setPersons(persons.filter(person => person.id !== id))
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter 
        value={filter} 
        handler={handleFilterChange}
      />

      <h2>Add new</h2>

      <PersonForm 
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        handleNameChange={handleNameChange}
        handleNumberChange={handleNumberChange}
      />

      <h2>Numbers</h2>

      <FilteredPersons 
        filter={filter} 
        persons={persons} 
        removePerson={removePerson}
      />
    </div>
  )
}

export default App
